import React, { Fragment, Component } from "react";
import { Card, ListGroup } from "react-bootstrap";
import { Button } from "@material-ui/core";
import mydata from "../HospData";
import { HospModal } from "./HospModal";
import axios from "axios";

export default class Cards extends Component {
  constructor(props) {
    super(props);
    this.state = { modalShow: false, hospd: {} };
  }

  handleButton = () => {
    axios({
      method: "GET",
      url: "http://localhost:5000/api/hospital/getHospitals",
      headers: {
        "Content-Type": "application/json",
      },
    }).then((res) => {
      this.hospd = res.data;
      console.log(this.hospd);
    });
  };

  render() {
    const styles = {
      Card: {
        marginTop: 5,
        marginBottom: 5,
      },
      Button: { marginLeft: 20, marginRight: 20 },
      LI: { marginLeft: 30, marginRight: 30 },
    };
    let modalClose = () => this.setState({ modalShow: false });

    const items = this.state.hospd.map((items) => <li>{items}</li>);
    return (
      <div>
        <textarea
          onChange={this.handleChange}
          value={this.state.userInput}
          placeholder="Separate Items With Commas"
        />
        <br />
        <button onClick={this.handleSubmit}>Create List</button>
        <h1>My "To Do" List:</h1>
        <ul>{this.items}</ul>
      </div>
    );

