import React, { Fragment, Component } from "react";
import { Card, ListGroup } from "react-bootstrap";
import { Button } from "@material-ui/core";
import mydata from "../HospData";
import { HospModal } from "./HospModal";

export default class Cards extends Component {
  constructor(props) {
    super(props);
    this.state = { modalShow: false };
  }

  render() {
    const styles = {
      Card: {
        marginTop: 5,
        marginBottom: 5,
      },
      Button: { marginLeft: 20, marginRight: 20 },
      LI: { marginLeft: 30, marginRight: 30 },
    };
    let modalClose = () => this.setState({ modalShow: false });

    return mydata.map((mydata) => {
      return (
        <Card style={styles.Card}>
          <Card.Header style={{ fontSize: 30 }}>{mydata.hname}</Card.Header>
          <ListGroup variant="flush">
            <ListGroup.Item style={styles.LI}>
              ICUs vacant: {mydata.icus}
            </ListGroup.Item>
            <ListGroup.Item style={styles.LI}>
              Non ICUs vacant: {mydata.nonicus}
            </ListGroup.Item>
            <ListGroup.Item style={styles.LI}>
              Ventilators available: {mydata.ventilators}
            </ListGroup.Item>
          </ListGroup>
          <Card.Body>
            <>
              <Button
                style={styles.Button}
                variant="contained"
                color="primary"
                onClick={() => this.setState({ modalShow: true })}
              >
                Book Now
              </Button>
              <HospModal show={this.state.modalShow} onHide={modalClose} />
            </>
            <Button
              style={styles.Button}
              variant="outlined"
              color="primary"
              href="#calls"
            >
              For More Information Call: {mydata.number}
            </Button>
          </Card.Body>
          <Card.Footer className="text-muted">
            Last Updated: 1 hour ago
          </Card.Footer>
        </Card>
      );
    });
  }
}
