import React from "react";
import { Card, ListGroup } from "react-bootstrap";
import { Button } from "@material-ui/core";
import mydata from "../HospData";
import ReactDOM from "react-dom";

export default function Cards() {
  const styles = {
    Card: { marginTop: 5, marginBottom: 5 },
    Button: { marginLeft: 20, marginRight: 20 },
    LI: { marginLeft: 30, marginRight: 30 },
  };

  return mydata.map((mydata) => {
    return (
      <Card style={styles.Card}>
        <Card.Header style={{ fontSize: 30 }}>{mydata.hname}</Card.Header>
        <ListGroup variant="flush">
          <ListGroup.Item style={styles.LI}>
            ICUs vacant: {mydata.icus}
          </ListGroup.Item>
          <ListGroup.Item style={styles.LI}>
            Non ICUs vacant: {mydata.nonicus}
          </ListGroup.Item>
          <ListGroup.Item style={styles.LI}>
            Ventilators available: {mydata.ventilators}
          </ListGroup.Item>
        </ListGroup>
        <Card.Body>
          <Button
            style={styles.Button}
            variant="contained"
            color="primary"
            href="#opens-form"
          >
            Book Now
          </Button>
          <Button
            style={styles.Button}
            variant="contained"
            color="primary"
            href="#calls"
          >
            Call
          </Button>
        </Card.Body>
      </Card>
    );
  });
}
